int fast_magnitude(int gx, int gy) {
    int abs_gx = abs(gx);
    int abs_gy = abs(gy);
    if (abs_gx > abs_gy) {
        return abs_gx + (abs_gy >> 1);
    } else {
        return abs_gy + (abs_gx >> 1);
    }
}

void apply_sobel(const uint8_t input[][WIDTH], uint8_t output[][WIDTH]) {
    memset(output, 0, HEIGHT * WIDTH * sizeof(uint8_t));

    for (int y = 1; y < HEIGHT-1; y++) {
        for (int x = 1; x < WIDTH-1; x++) {
            int16_t gx = 0, gy = 0;
            for (int ky = -1; ky <= 1; ky++) {
                for (int kx = -1; kx <= 1; kx++) {
                    gx += input[y + ky][x + kx] * sobel_x[ky + 1][kx + 1];
                    gy += input[y + ky][x + kx] * sobel_y[ky + 1][kx + 1];
                }
            }
            int magnitude = fast_magnitude(gx, gy);
            output[y][x] = (magnitude > 255) ? 255 : magnitude;
        }
    }
}

void app_main() {
    uint8_t (*sobel_output)[WIDTH] = malloc(HEIGHT * WIDTH * sizeof(uint8_t));
    if (!sobel_output) {
        printf("Error al asignar memoria\n");
        return;
    }

    apply_sobel(image, sobel_output);

    // Imprimir toda la matriz en el formato deseado
    printf("uint8_t sobel_output[%d][%d] = {\n", HEIGHT, WIDTH);
    for (int y = 0; y < HEIGHT; y++) {
        printf("    {");
        for (int x = 0; x < WIDTH; x++) {
            printf("%3d", sobel_output[y][x]);
            if (x < WIDTH - 1) printf(", ");
        }
        printf("}%s\n", (y < HEIGHT - 1) ? "," : "");
    }
    printf("};\n");

    free(sobel_output);

    printf("\nOperación completada. Reinicia el ESP32 para ejecutar nuevamente.\n");
    while (1) {
        vTaskDelay(1000 / portTICK_PERIOD_MS);
    }
}